import React, { Component } from 'react'

export default class Cell extends Component {
    static defaultProps = {
        flipCellsAroundMe: ()=>{},
        isLit : false
    }
    constructor(props) {
      super(props)

      this.handleClick = this.handleClick.bind(this.handleClick)
    }


    handleClick(e){
        this.props.flipCellsAroundMe()
    }

    lit = (bool) => bool === false?'':'lit' 


  render() {
    return (
      <div className= {`Cell ${this.lit(this.props.isLit)}`} onClick={this.handleClick}>

      </div>
    )
  }
}
